mapper.select:
SELECT COUNT(*)
FROM deliveries d
JOIN delivery_history dh ON d.deliveries_id = dh.deliveries_id
WHERE d.raiders_id = #{raiders_id}
AND dh.delivery_history_status = '배달 완료';

SELECT COUNT(*)
FROM commissions
WHERE raiders_id = #{raiders_id}
AND deliveries_id = #{deliveries_id};

SELECT d.deliveries_id, r.rider_name, o.orders_id, o.orders_price, dh.delivery_history_status, o.orders_cdate, s.shop_name, a.addr_line1, a.addr_line2, COALESCE(c.commission, o.orders_price * 0.03) AS commission FROM deliveries d JOIN riders r ON d.raiders_id = r.raiders_id JOIN orders o ON d.orders_id = o.orders_id JOIN delivery_history dh ON d.deliveries_id = dh.deliveries_id JOIN shop s ON o.shop_id = s.shop_id JOIN addr a ON o.addr_id = a.addr_id LEFT JOIN commissions c ON d.deliveries_id = c.deliveries_id AND d.raiders_id = c.raiders_id WHERE d.raiders_id = #{raiders_id} AND dh.delivery_history_status = '배달 완료'
AND o.orders_cdate >= #{startDate}
AND o.orders_cdate <= #{endDate}
ORDER BY o.orders_cdate DESC LIMIT #{offset}, #{blockList};

SELECT COUNT(*)
FROM deliveries d
JOIN delivery_history dh ON d.deliveries_id = dh.deliveries_id
JOIN orders o ON d.orders_id = o.orders_id
WHERE d.raiders_id = #{raiders_id}
AND dh.delivery_history_status = '배달 완료'

    AND o.orders_cdate >= #{startDate}


    AND o.orders_cdate <= #{endDate}

SELECT COALESCE(SUM(
    CASE
        WHEN c.commission IS NOT NULL THEN c.commission
        ELSE o.orders_price * 0.03
    END
), 0)
FROM deliveries d
JOIN orders o ON d.orders_id = o.orders_id
LEFT JOIN commissions c ON d.deliveries_id = c.deliveries_id AND d.raiders_id = c.raiders_id
WHERE d.raiders_id = #{raiders_id}
AND EXISTS (SELECT 1 FROM Delivery_history dh WHERE dh.deliveries_id = d.deliveries_id AND dh.delivery_history_status = '배달 완료')

    AND o.orders_cdate >= #{startDate}


    AND o.orders_cdate <= #{endDate}
