mapper.insert:
INSERT INTO coupon (member_id, coupon_code, discount_amount, issued_date)
SELECT m.member_id, LEFT(UUID(), 8), g.grade_discount, NOW()
FROM member m
JOIN grade g ON m.grade_id = g.grade_id;
WHERE m.member_id = #{memberId};

mapper.select:
SELECT coupon_code, discount_amount, issued_date, used_date 
FROM coupon 
WHERE member_id = #{member_id};

SELECT c.coupon_code, c.discount_amount, c.issued_date, c.used_date, m.member_name 
FROM coupon c 
JOIN member m ON c.member_id = m.member_id;
