mapper.insert:
INSERT INTO coupon (member_id, coupon_code, discount_amount, start_date)
SELECT m.member_id, LEFT(UUID(), 8), #{discount_amount}, NOW()
FROM member m;

mapper.select:
SELECT coupon_code, discount_amount, start_date, used_date
FROM coupon
WHERE member_id = #{member_id};

SELECT c.coupon_code, c.discount_amount, c.start_date, c.used_date, m.member_name
FROM coupon c
JOIN member m ON c.member_id = m.member_id;
