mapper.select:
select * from inquiries


            inquiries_type=#{inquiries_type}


order by inquiries_id desc

mapper.update:
update inquiries set inquiries_title=#{inquiries_title},
                     inquiries_content=#{inquiries_content},
                     inquiries_type=#{inquiries_type},
                     member_id=#{member_id},
                     owner_id=#{owner_id}
                     where inquiries_id=#{inquiries_id}
