mapper.select:
SELECT s.*, (SELECT COUNT(*) FROM menu WHERE shop_id = s.shop_id) AS menu_count, (SELECT COUNT(*) FROM reviews r INNER JOIN orders o ON o.orders_id = r.orders_id WHERE o.shop_id = s.shop_id) AS reviews_count, (SELECT AVG(r.reviews_rating) FROM reviews r INNER JOIN orders o ON o.orders_id = r.orders_id WHERE o.shop_id = s.shop_id) AS shop_rating, (SELECT COUNT(*) FROM ceoreview c INNER JOIN reviews r ON c.reviews_id = r.reviews_id INNER JOIN orders o ON o.orders_id = r.orders_id WHERE o.shop_id = s.shop_id) AS ceoreview_count
FROM shop s
AND food_category = #{foodCategory}

mapper.update:
update shop set shop_name = #{shop_name}, shop_content = #{shop_content}, shop_addr1 = #{shop_addr1}, shop_addr2 = #{shop_addr2}, shop_call = #{shop_call}, shop_min_price = #{shop_min_price}, shop_open_hour = #{shop_open_hour}, shop_close_hour = #{shop_close_hour}, shop_close_day = #{shop_close_day}, food_category = #{food_category}
, shop_img = #{shop_img}
where shop_id = #{shop_id}
